<script lang="ts" setup>

interface PageElement extends HTMLElement {
    pageNum?: number;
}

const onPageFlipOrUnFlip = () => {
    const pages = document.getElementsByClassName('book-page') as HTMLCollectionOf<PageElement>;

    if (pages) {
        for (let i = 0; i < pages.length; i++) {
            const page = pages[i];
            if (i % 2 === 0) {
                page.style.zIndex = String(pages.length - i);
            }
        }

        for (let i = 0; i < pages.length; i++) {
            const page = pages[i];
            page.pageNum = i + 1;

            page.onclick = () => {
                if (page.pageNum && page.pageNum % 2 === 0) {
                    page.classList.remove('flipped');
                    const previousPage = page.previousElementSibling as HTMLElement | null;
                    if (previousPage) {
                        previousPage.classList.remove('flipped');
                    }
                } else {
                    page.classList.add('flipped');
                    const nextPage = page.nextElementSibling as HTMLElement | null;
                    if (nextPage) {
                        nextPage.classList.add('flipped');
                    }
                }
            };
        }

    }
}

onMounted(() => {
    const pages = document.getElementsByClassName('book-page') as HTMLCollectionOf<PageElement>;

    if (pages) {
        for (let i = 0; i < pages.length; i++) {
            const page = pages[i];
            if (i % 2 === 0) {
                page.style.zIndex = String(pages.length - i);
