<style lang="scss" scoped>
.book {
    transition: opacity 0.4s 0.2s;
}

.book-page {
    width: 40vw;
    height: 55vw;
    float: left;
    margin-bottom: 0.5em;
    background: top no-repeat;
    background-size: cover;
}

.book-page:nth-child(even) {
    clear: both;
}

.book {
    perspective: 250vw;
}

.book .pages {
    width: 80vw;
    height: 55vw;
    position: relative;
    transform-style: preserve-3d;
    backface-visibility: hidden;
    border-radius: 4px;
}

.book .book-page {
    float: none;
    clear: none;
    margin: 0;
    position: absolute;
    top: 0;
    width: 40vw;
    height: 55vw;
    transform-origin: 0 0;
    transition: transform 1.4s;
    backface-visibility: hidden;
    transform-style: preserve-3d;
    cursor: pointer;
    user-select: none;
    background-color: #f0f0f0;
}

.book .book-page:before {
    content: '';
    position: absolute;
    top: 0;
    bottom: 0;
    left: 0;
    right: 0;
    transition: background 0.7s;
    z-index: 2;
}

.book .book-page:nth-child(odd) {
    pointer-events: all;
    transform: rotateY(0deg);
    right: 0;
    border-radius: 0 4px 4px 0;
}

.book .book-page:nth-child(odd):hover {
    transform: rotateY(-15deg);
}

.book .book-page:nth-child(odd):hover:before {
    background: rgba(0, 0, 0, 0.03);
}

.book .book-page:nth-child(odd):before {
    background: rgba(0, 0, 0, 0);
}

.book .book-page:nth-child(even) {
    pointer-events: none;
    transform: rotateY(180deg);
    transform-origin: 100% 0;
    left: 0;
    border-radius: 4px 0 0 4px;
    border-color: black;
}

.book .book-page:nth-child(even):before {
    background: rgba(0, 0, 0, 0.2);
}

.book .book-page.grabbing {
    transition: none;
}

.book .book-page.flipped:nth-child(odd) {
    pointer-events: none;
    transform: rotateY(-180deg);
}

.book .book-page.flipped:nth-child(odd):before {
    background: rgba(0, 0, 0, 0.2);
}

.book .book-page.flipped:nth-child(even) {
    pointer-events: all;
    transform: rotateY(0deg);
}

.book .book-page.flipped:nth-child(even):hover {
    transform: rotateY(45deg);
}

.book .book-page.flipped:nth-child(even):hover:before {
    background: rgba(0, 0, 0, 0.03);
}

.book .book-page.flipped:nth-child(even):before {
    background: rgba(0, 0, 0, 0);
}

.book-page:nth-child(odd) {
    background-position: right top;
}
</style>
